package com.hdeva.nytimes.service

import com.hdeva.nytimes.net.converter.NyTimesArticlesConverter
import com.hdeva.nytimes.net.dto.NyTimesArticlesDto
import com.squareup.moshi.Moshi
import org.junit.Assert.assertEquals
import org.junit.Test

class ArticleImageExtractorTest {

    @Test
    fun test_extract_meta_data_found() {
        val sut = ArticleImageExtractor()

        val dto = Moshi.Builder().build().adapter(NyTimesArticlesDto::class.java).fromJson(TEST_DATA)
        val model = NyTimesArticlesConverter().fromJson(dto)

        val square320 = sut.extractSquare320(model.articles[0])
        assertEquals(square320?.format, "square320")
    }

    @Test
    fun test_extract_meta_data_not_found() {
        val sut = ArticleImageExtractor()

        val dto = Moshi.Builder().build().adapter(NyTimesArticlesDto::class.java).fromJson(TEST_DATA)
        val model = NyTimesArticlesConverter().fromJson(dto)

        val notFound = sut.doFilter(model.articles[0], "randomTypeWhichShouldNotExist")
        assertEquals(notFound, null)
    }

